// Accede a los datos del nodo Webhook
// Si usas GET, los datos están en la propiedad 'query'.
const params = $node["Webhook"].json.query;

// Array de parámetros permitidos
const allowedParams = ['location', 'budget_min', 'budget_max'];

// 1. Verificación de si el objeto de parámetros está vacío
if (Object.keys(params).length === 0) {
  return [{ json: { message: "No parameters received. All filters are optional, but if you send parameters, they must be valid." } }];
}

// 2. Verificación de parámetros extra no permitidos
const receivedParams = Object.keys(params);
const invalidParams = receivedParams.filter(p => !allowedParams.includes(p));

if (invalidParams.length > 0) {
  return[{
    json: {
      message_error: `Parametros invalidos recibidos: '${invalidParams.join(', ')}'. Solo se permiten 'location', 'budget_min', y 'budget_max'.`
    }
  }]
}

// Variables para los filtros validados
let location = null;
let budget_min = parseInt(params.budget_min, 10);
let budget_max = parseInt(params.budget_max, 10);;

// Asignación de locación
if (params.location) {
  location = params.location;
}

// Validación de budget_min
if (params.budget_min) {
  if (isNaN(budget_min) || budget_min < 0) {
    return [{
      json: {
        error: `Invalid budget_min: ${params.budget_min}. Must be a positive number.`
      }
    }];
  }
}

// Validación de budget_max
if (params.budget_max) {
  if (isNaN(budget_max) || budget_max < 0) {
    return [{
      json: {
        error: `Invalid budget_max: ${params.budget_max}. Must be a positive number.`
      }
    }];
  }
}

if (budget_max<budget_min) {
return [{
    json: {
      error: `Invalid budget range: budget_max (${budget_max}) cannot be less than budget_min (${budget_min}).`
    }
  }];}


// Devuelve los parámetros validados para que los use el resto del flujo
return [{
  json: {
    location: location,
    budget_min: budget_min,
    budget_max: budget_max
  }
}];

