// Simplifica el acceso a los datos del nodo
const leads = items.map(item => item.json);

// 1. Calcula el presupuesto total
// El método reduce itera sobre el array y suma cada valor de 'budget'.
const totalBudget = leads.reduce((sum, lead) => {
  // Asegúrate de convertir el 'budget' a un número para la suma.
  return sum + parseInt(lead.budget);
}, 0); // El 0 es el valor inicial de la suma

// 2. Ordena los leads de forma descendente por presupuesto
const sortedLeads = leads.sort((a, b) => {
  // Convierte los valores a números para asegurar un ordenamiento correcto
  return parseInt(b.budget) - parseInt(a.budget);
});

// 3. Retorna un único objeto JSON con los dos resultados
return [{
  json: {
    total_budget: totalBudget,
    sorted_leads: sortedLeads
  }
}];

